mapper.select:


	1=1

		AND "osv".SV_NAME like concat(concat('%', #{svName}), '%')


		AND "osv".SCN_SYS_ID = #{scnSysId}


		AND "osv".SV_STATUS = #{svStatus}


		AND "osv".AUDIT_STATE = #{auditState}


		AND "osv".STD_FIELD_CHECK = #{stdFieldCheck}


		AND "osv".JK_ADAP_FLAG = #{jkAdapFlag}


		AND "osv".SV_TYPE = #{svType}


		AND "osv".XML_VALID_FLAG = #{xmlValidFlag}


		AND "osv".SERVICE_ID = #{serviceId}


		AND "osv".SV_UPDATE_TIME >= #{startTime}


		AND "osv".SV_UPDATE_TIME <= #{endTime}


order by "osv".SV_UPDATE_TIME desc

SELECT RESOURCE_NAME as resourceName
FROM aesb_g_scene_resource
WHERE SCENE_VERSION_ID = #{svId}
  and RESOURCE_TYPE = #{resourceType}

select SV_NAME as "svName"
from opr_scenario_version
where SV_ID = #{svId}

SELECT SV_ID as "svId"
FROM opr_scenario_version
WHERE SV_CODE = #{svCode}
  AND SV_NAME = #{svName}
  AND SV_NO = (
	SELECT max(SV_NO) FROM opr_scenario_version WHERE SV_CODE = #{svCode} AND SV_NAME = #{svName}
)

mapper.insert:
begin
INSERT INTO opr_scenario_version ( SV_SCN_ID,SV_ID,SV_NO,SV_TYPE,SV_CODE,SV_NAME,SV_INTERFACE,SCN_SYS_ID,RESPONSE_FLAG, SERVICE_ID,SV_SV_ID,PROJECT_CODE,APPLICATION_ID,XML_VALID_FLAG,STD_FIELD_CHECK, JK_ADAP_FLAG,JK_ADAP_TYPE,LOCK_TYPE,UPDATE_REASON,UPDATE_CONTENT,AUDIT_STATE,SV_STATUS, SV_CREATE_TIME,SV_UPDATE_TIME ) values( #{item.svScnId}, #{item.svId}, #{item.svNo}, #{item.svType}, #{item.svCode},#{item.svName},#{item.svInterface},#{item.scnSysId},#{item.responseFlag}, #{item.serviceId}, #{item.svSvId},#{item.projectCode},#{item.applicationId,jdbcType=VARCHAR},#{item.xmlValidFlag},#{item.stdFieldCheck},#{item.jkAdapFlag},#{item.jkAdapType}, #{item.lockType},#{item.updateReason},#{item.updateContent}, #{item.auditState}, #{item.svStatus}, #{item.svCreateTime},sysdate );
end;

begin

	INSERT INTO opr_scenario_version_param
	(SCENE_ID,SCENE_VERSION_ID,SCENE_PRIORITY,SCENE_TIMEOUT) values
	(
	#{item.sceneId}, #{item.sceneVersionId}, #{item.scenePriority},#{item.sceneTimeout}
	);

end;

mapper.update:
update opr_scenario_version_param
SCENE_ID = #{sceneId}, SCENE_VERSION_ID = #{sceneVersionId}, SCENE_DUTY_TIME = #{sceneDutyTime}, SCENE_WINDOW_TIME = #{sceneWindowTime}, SCENE_TIMEOUT = #{sceneTimeout}, ONE_TIME_USER_NUM = #{oneTimeUserNum}, TRADE_PER_SECOND = #{tradePerSecond}, SCENE_AVAILABLE = #{sceneAvailable}, SCENE_FIX_TIME = #{sceneFixTime}, RSP_TIME = #{rspTime}, SCENE_PRIORITY = #{scenePriority}, ONLINE_DATE = #{onlineDate}, OFFLINE_DATE = #{offlineDate}, IS_OPEN = #{isOpen}, METHOD_NAME = #{methodName}, API_TYPE = #{apiType}, AUTH_FLAG = #{authFlag}, FREE_FLAG = #{freeFlag}, REQUEST_URL = #{requestUrl}, API_PRICE = #{apiPrice}, API_VERSION = #{apiVersion}, HTTP_METHOD = #{httpMethod}, CHECK_FLAG = #{checkFlag}, DATA_SOURCE = #{dataSource},
where SCENE_VERSION_ID = #{sceneVersionId}

update OPR_SCENARIO_FIELD
SCN_CODE = #{scnCode}, SF_COUNT = #{sfCount}, SF_EN_NAME = #{sfEnName}, SF_CN_NAME = #{sfCnName}, SF_TYPE = #{sfType}, SF_DATA_TYPE = #{sfDataType}, SF_LENGTH = #{sfLength}, SF_FRACTION = #{sfFraction}, SF_IS_ENSSENTIAL = #{sfIsEnssential}, SF_CYCLE_TIMES = #{sfCycleTimes}, SF_FORMAT = #{sfFormat}, SF_ENUM = #{sfEnum}, SF_DES = #{sfDes}, SF_DEFAULT = #{sfDefault}, SF_IS_SENSITIVE = #{sfIsSensitive}, SF_PATH = #{sfPath}, SF_LENGTH_TYPE = #{sfLengthType},
where SF_SV_ID = #{sfSvId};

mapper.select:
select count(1)
from OPR_SCENARIO_FIELD
where SF_SV_ID = #{sfSvId}
  and SF_TYPE = #{sfType}
